choose.when:
SELECT DISTINCT patient_id FROM t_outpatient_info WHERE 1=1
AND dept_code = #{queryFilter.deptCode}
AND first_diagnosis_time BETWEEN TO_DATE(#{queryFilter.startDate}, 'YYYY-MM-DD') AND TO_DATE(#{queryFilter.endDate}, 'YYYY-MM-DD')

SELECT DISTINCT patient_id FROM t_pats_in_hospital WHERE 1=1
AND dept_admission_to_code = #{queryFilter.deptCode}
AND discharge_date_time BETWEEN TO_DATE(#{queryFilter.startDate}, 'YYYY-MM-DD') AND TO_DATE(#{queryFilter.endDate}, 'YYYY-MM-DD')

SELECT DISTINCT patient_id FROM t_operation_master WHERE 1=1
AND exec_dept_code = #{queryFilter.deptCode}
AND scheduled_date_time BETWEEN TO_DATE(#{queryFilter.startDate}, 'YYYY-MM-DD') AND TO_DATE(#{queryFilter.endDate}, 'YYYY-MM-DD')

select.if:
SELECT COUNT(DISTINCT patient_id)
FROM t_follow_execute_plan
WHERE patient_id IN

    #{id}

AND status != '0'

    AND plan_answer_id = #{queryFilter.surveyId}

SELECT COUNT(DISTINCT patient_id)
FROM t_follow_up_record
WHERE patient_id IN

    #{id}

AND answer_status = '1'

    AND plan_answer_id = #{queryFilter.surveyId}

SELECT ROUND(SUM(CASE WHEN avg_score >= 3 THEN 1 ELSE 0 END) / NULLIF(COUNT(*), 0), 2) * 100 AS totalSatisfactionRate FROM ( -- 计算每个答卷的平均分 SELECT r.result_answer_id, AVG(s.answer_score) AS avg_score FROM t_an_score s JOIN t_question q ON s.qu_id = q.qu_id JOIN t_follow_up_record r ON s.belong_answer_id = r.result_answer_id WHERE q.satisfaction_type = '1' AND r.patient_id IN
#{id}
AND r.plan_answer_id = #{queryFilter.surveyId}
GROUP BY r.result_answer_id ) avg_scores

SELECT * FROM ( SELECT option_name AS optionName, ROUND(SUM(CASE WHEN s.answer_score >= 3 THEN 1 ELSE 0 END) / NULLIF(COUNT(*), 0), 2) * 100 AS satisfactionRate FROM t_an_score s JOIN t_qu_score o ON s.qu_row_id = o.score_qu_id
o.qu_id = #{queryFilter.surveyId}
AND s.belong_answer_id IN ( SELECT result_answer_id FROM t_follow_up_record WHERE patient_id IN
#{id}
)
GROUP BY option_name ORDER BY satisfactionRate DESC ) WHERE ROWNUM <= 3

SELECT * FROM ( SELECT option_name AS optionName, ROUND(SUM(CASE WHEN s.answer_score >= 3 THEN 1 ELSE 0 END) / NULLIF(COUNT(*), 0), 2) * 100 AS satisfactionRate FROM t_an_score s JOIN t_qu_score o ON s.qu_row_id = o.score_qu_id
o.qu_id = #{queryFilter.surveyId}
AND s.belong_answer_id IN ( SELECT result_answer_id FROM t_follow_up_record WHERE patient_id IN
#{id}
)
GROUP BY option_name ORDER BY satisfactionRate ASC ) WHERE ROWNUM <= 3
